"use strict";class Dialog{constructor(n){this.width=600;this.maxWidth=800;this.$dialog=$(`#${n}`);this.$dialog.addClass("dialog");this.$dialog.dialog({autoOpen:!1,width:this.width,maxWidth:this.maxWidth});this.$dialog.find(".message").html("&nbsp;")}open(){this.$dialog&&this.$dialog.dialog("open")}isOpen(){return this.$dialog?this.$dialog.dialog("isOpen"):!1}close(){this.$dialog&&this.$dialog.dialog("close")}message(n){this.$dialog.find(".message").html(n).addClass("highlight");setInterval(()=>this.clearMessage(),5e3)}clearMessage(){this.$dialog.find(".message").html("&nbsp;").removeClass("highlight")}}