"use strict";class LookupDialog extends Dialog{constructor(n,t){super(n);this.$dialog.on("dialogopen",n=>this.dialogOpened(n));this.parent=t;this.$dialog.find("[button-type='apply']").on("click",()=>this.apply());this.$dialog.find("[button-type='cancel']").on("click",()=>this.close());this.title=this.$dialog.dialog("option","title")}dialogOpened(){this.$dialog.dialog("option","width",320)}update(n,t){this.$input=t;this.columnIndex=parseInt(t.attr("columnIndex"));let i=this.parent.columns.find(n=>n.index==this.columnIndex);this.$dialog.dialog("option","title",`${i.label} ${this.title}`);this.$dialog.find("div.select").html(n.data);this.$dialog.find("select").val(t.val().split(",")).width(300)}apply(){var n=this.$dialog.find("select"),t=n.val();this.$input.val(t.join(",")).trigger("keyup");this.close()}}